  const getDateOfBirthNumber = (dob) => {
    if (typeof dob !== 'string') {
      console.error('Invalid input: DOB must be a string');
      return 0; // Return a default value if DOB is not a string
    }
  
    // Split the DOB into components (assuming format is YYYY-MM-DD)
    const [year, month, day] = dob.split('-').map(Number);
  
    // Sum the digits of the year, month, and day
    const sumDigits = (number) => {
      return number.toString().split('').reduce((acc, digit) => acc + parseInt(digit), 0);
    };
  
    // Calculate the total sum of digits from year, month, and day
    let totalSum = sumDigits(year) + sumDigits(month) + sumDigits(day);
  
    // Reduce the total sum to a single digit
    const reduceToSingleDigit = (number) => {
      while (number >= 10) {
        number = number.toString().split('').reduce((acc, digit) => acc + parseInt(digit), 0);
      }
      return number;
    };
  
    return reduceToSingleDigit(totalSum);
  };

